import React, { Component } from 'react';
import { connect } from 'react-redux';
import './HomeHeader.scss';
import logo from '../../assets/images/bookingcare-2020.svg';
import {FormattedMessage} from 'react-intl';
import {LANGUAGES} from "../../utils";
import { changeLanguageApp } from '../../store/actions';
import {withRouter} from 'react-router';
class HeaderHome extends Component {

    changeLanguage = (language) => {
        //fire redux event : actions
        this.props.changeLanguageAppRedux(language);
    }
    //bootstrap = đã khởi động thành công
    returnToHome = () => {
        if (this.props.history) {
            this.props.history.push(`/home`)
        }
    }

    render() {
        let language = this.props.language;
        console.log('check userinfo:', this.props.userInfo);
        return (
            <React.Fragment>
            <div className="header-home-container">
                <div className="home-header-content">
                    <div className="left-content">
                        <i className="fa fa-bars"></i>
                        <img className="header-logo" src={logo} onClick={()=> this.returnToHome()}/>
                        
                    </div>
                    <div className="center-content">
                        <div className="child-content">
                            <div><b><FormattedMessage id="homeheader.speciality"/></b></div>
                            <div className="sub-title"><FormattedMessage id="homeheader.searchdoctor"/></div>
                        </div>
                        <div className="child-content">
                            <div><b><FormattedMessage id="homeheader.health-facility"/></b></div>
                            <div className="sub-title"><FormattedMessage id="homeheader.select-room"/></div>
                        </div>
                        <div className="child-content">
                            <div><b><FormattedMessage id="homeheader.doctor"/></b></div>
                            <div className="sub-title"><FormattedMessage id="homeheader.select-doctor"/></div>
                        </div>
                        <div className="child-content">
                            <div><b><FormattedMessage id="homeheader.fee"/></b></div>
                            <div className="sub-title"><FormattedMessage id="homeheader.check-health"/></div>
                        </div>
                    </div>
                    <div className="right-content">
                        <div className="support"> <i className="fa fa-question-circle"></i><FormattedMessage id="homeheader.support"/></div>
                        <div className={language === LANGUAGES.VI ? 'language-vi active' : 'language-vi'}>
                            <span onClick={() => this.changeLanguage(LANGUAGES.VI)}>VN</span>
                        </div>
                        <div className={language === LANGUAGES.EN ? 'language-en active' : 'language-en'}>
                            <span onClick={() => this.changeLanguage(LANGUAGES.EN)}>EN</span></div>
                    </div>

                </div>
            </div>
            {this.props.isShowBanner == true &&
                <div className="home-header-banner">
                    <div className="content-up">
                        <div className="title1"><FormattedMessage id="banner.title1"/></div>
                        <div className="title2"><FormattedMessage id="banner.title2"/> </div>
                        <div className="search">
                            <i className="fas fa-search"></i>
                            <input type="text" placeholder="Tìm chuyên khoa khám bệnh"/>
                        </div>
                    </div>
                    <div className="content-down">
                        <div className="options">
                            <div className="option-child">
                                <div className="icon-child"><i className="fa fa-hospital"></i></div>
                                <div className="text-child"><FormattedMessage id="banner.child1"/></div>
                            </div>
                            <div className="option-child">
                                <div className="icon-child"><i className="fa fa-hospital"></i></div>
                                <div className="text-child"><FormattedMessage id="banner.child2"/></div>
                            </div>
                            <div className="option-child">
                                <div className="icon-child"><i className="fa fa-hospital"></i></div>
                                <div className="text-child"><FormattedMessage id="banner.child3"/></div>
                            </div>
                            <div className="option-child">
                                <div className="icon-child"><i className="fa fa-hospital"></i></div>
                                <div className="text-child"><FormattedMessage id="banner.child4"/></div>
                            </div>
                            <div className="option-child">
                                <div className="icon-child"><i className="fa fa-hospital"></i></div>
                                <div className="text-child"><FormattedMessage id="banner.child5"/></div>
                            </div>
                            <div className="option-child">
                                <div className="icon-child"><i className="fa fa-hospital"></i></div>
                                <div className="text-child"><FormattedMessage id="banner.child6"/></div>
                            </div>
                        </div>
                    </div>
                </div>
            }    
            </React.Fragment>
        );
    }
}
//state cua ham reduce
//map state cua redux và tiem vao react , react co the lay state qua this.props
const mapStateToProps = state => {
    return {
        isLoggedIn: state.user.isLoggedIn,
        userInfo: state.user.userInfo,
        language: state.app.language,
    };
};
//fire event cua redux
const mapDispatchToProps = dispatch => {
    return {
        changeLanguageAppRedux: (language) => dispatch(changeLanguageApp(language))
    };
};

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(HeaderHome));
