import React, { Component } from 'react';
import { FormattedMessage } from 'react-intl';
import { connect } from 'react-redux';
import * as actions from "../../../store/actions";
import MarkdownIt from 'markdown-it';
import MdEditor from 'react-markdown-editor-lite';
//import style manually
import 'react-markdown-editor-lite/lib/index.css';
import './ManageDoctor.scss';
import Select from 'react-select';
import {LANGUAGES, CRUD_Actions} from '../../../utils';
import {getDetailInforDoctor} from "../../../services/userService"

const options = [
    {value: 'chocolate', label: 'Chocolate'},
    {value: 'strawberry', label:'Strawberry'},
    {value:'vanilla', label:'Vanilla'},
];
const mdParser = new MarkdownIt();

class ManageDoctor extends Component {
    constructor(props) {
        super(props);
        this.state = {
            doctorId:'',
            contentMarkdown:'',
            contentHTML:'',
            selectedOption:'',
            description:'',
            listDoctors:[],
            hasOldData: false,

            listPrice:[],
            listPayment: [],
            listProvince: [],
            selectedPrice: '',
            selectedPayment: '',
            selectedProvince: '',
            nameClinic: '',
            addressClinic: '',
            note: ''
        }
    }
    componentDidMount() {
        this.props.fetchAllDoctors();
        this.props.getAllRequiredDoctorInfor();
    }

    buildDataInputSelect = (inputData, type) => {
        let result = [];
        let {language} = this.props;
        if(inputData && inputData.length > 0){
            if(type ==="USERS"){
                inputData.map((item, index)=>{
                    let object = {};
                    let labelVi = type === 'USERS' ? `${item.lastName} ${item.firstName}` : item.valueVI;
                    let labelEn = type === 'USERS' ? `${item.firstName} ${item.lastName}` : item.valueEN;
                    object.label = language === LANGUAGES.VI ? labelVi : labelEn;
                    object.value = item.id;
                    result.push(object);
                })
            }
            
            if (type === 'PRICE'){
                inputData.map((item, index)=>{
                    let object = {};
                    let labelVi = `${item.valueVI}`;
                    let labelEn = `${item.valueEN} USD`;
                    object.label= language===LANGUAGES.VI ? labelVi: labelEn;
                    object.value= item.keyMap;
                    result.push(object)
                })
            }    
            if(type === 'PAYMENT' || type === 'PROVINCE'){
                inputData.map((item, index)=>{
                    let object = {};
                    let labelVi = `${item.valueVI}`;
                    let labelEn = `${item.valueEN}`;
                    object.label = language === LANGUAGES.VI ? labelVi :  labelEn;
                    object.value = item.keyMap;
                    result.push(object)
                })
            }
        }
        return result;
    }

    componentDidUpdate(prevProps, prevState, snapshot) {
        if(prevProps.allDoctors !== this.props.allDoctors){
            let dataSelect = this.buildDataInputSelect(this.props.allDoctors, 'USERS')
            this.setState({
                listDoctors:dataSelect
            })
        }

        
        if(prevProps.allRequiredDoctorInfor !== this.props.allRequiredDoctorInfor){
            let {resPayment, resPrice, resProvince} = this.props.allRequiredDoctorInfor;
            let dataSelectPrice = this.buildDataInputSelect(resPrice, 'PRICE');
            let dataSelectPayment= this.buildDataInputSelect(resPayment,'PAYMENT');
            let dataSelectProvince = this.buildDataInputSelect(resProvince, 'PROVINCE');
            console.log('data new: ', dataSelectPrice, dataSelectPayment, dataSelectProvince);

            this.setState({
                listPrice: dataSelectPrice,
                listPayment: dataSelectPayment,
                listProvince: dataSelectProvince,
            })
        }

        if (prevProps.language !== this.props.language){
            let dataSelect = this.buildDataInputSelect(this.props.allDoctors, 'USERS');
            let {resPayment, resPrice, resProvince} = this.props.allRequiredDoctorInfor;
            let dataSelectPrice = this.buildDataInputSelect(resPrice, 'PRICE');
            let dataSelectPayment= this.buildDataInputSelect(resPayment,'PAYMENT');
            let dataSelectProvince = this.buildDataInputSelect(resProvince, 'PROVINCE');
            this.setState({
                listDoctors: dataSelect,
                listPrice: dataSelectPrice,
                listPayment: dataSelectPayment,
                listProvince: dataSelectProvince,
            })
        }
    }
    handleEditorChange = ({html, text}) => {
        this.setState({
            contentMarkdown: text,
            contentHTML: html,
        })
    }
    handleSaveContentMarkdown = () => {
        let {hasOldData} = this.state;
        this.props.saveDetailDoctors({
            doctorId: this.state.selectedOption.value,
            contentHTML: this.state.contentHTML,
            contentMarkdown: this.state.contentMarkdown,
            description:this.state.description,
            doctorId: this.state.selectedOption.value,
            action: hasOldData === true ? CRUD_Actions.EDIT : CRUD_Actions.CREATE,

            selectedPrice: this.state.selectedPrice.value,
            selectedPayment: this.state.selectedPayment.value,
            selectedProvince: this.state.selectedProvince.value,
            nameClinic: this.state.nameClinic,
            addressClinic: this.state.addressClinic,
            note: this.state.note,
            
        });
    }
    handleChangeSelect = async (selectedOption) => {
        this.setState({selectedOption});
        let res = await getDetailInforDoctor(selectedOption.value);
        if (res && res.errCode === 0 && res.data && res.data.Markdown) {
            let markdown = res.data.Markdown;
            this.setState({
                contentHTML: markdown.contentHTML,
                contentMarkdown: markdown.contentMarkdown,
                description: markdown.description,
                hasOldData: true
            })
        } else {
            this.setState({
                contentHTML: '',
                contentMarkdown:'',
                description: '',
                hasOldData:'',

            })
        }
    };
    handleChangeSelectDoctorInfor = async(selectedOption, name) => {
        let stateName = name.name;
        let stateCopy = {...this.state};
        stateCopy[stateName] = selectedOption;
        this.setState({
            ...stateCopy
        })
    }

    handleOnChangeText = (event, id)=> {
        let stateCopy = {...this.state};
        stateCopy[id] = event.target.value;
        this.setState({
            ...stateCopy
        })
    }

    handleOnChangeDesc = (event) => {
        this.setState({description: event.target.value})
    }
    
    render(){
        let {hasOldData} = this.state;
        console.log('check price: ', this.state.listPrice)
        return(
            
                <div className="manage-doctor-container">
                    <div className="manage-doctor-title">
                        <FormattedMessage id="admin.manage-doctor.title"/>
                    </div>
                    <div className="more-infor">
                        <div className="content-left form-group">
                            <label>< FormattedMessage id="admin.manage-doctor.select-doctor"/></label>
                            <Select
                                value={this.state.selectedOption}
                                onChange = {this.handleChangeSelect}
                                options ={this.state.listDoctors}
                                placeholder={<FormattedMessage id="admin.manage-doctor.select-doctor"/>}
                            />
                        </div>
                        <div className="content-right">
                            <label><FormattedMessage id="admin.manage-doctor.intro"/></label>
                            <textarea className="form-control" rows="4"
                                onChange = {(event) =>this.handleOnChangeText(event, 'description')}
                                value = {this.state.description}
                                >
                                </textarea>
                        </div>
                    </div>
                    <div className="more-infor-extra row">
                        <div className="col-4 form-group">
                            <label>Chon gia</label>
                            <Select
                                options={this.state.listPrice}
                                placeholder={<FormattedMessage id="admin.manage-doctor.price"/>}
                                name = "selectedPrice"
                            />
                            
                        </div>
                        <div className="col-4 form-group">
                            <label><FormattedMessage id="admin.manage-doctor.payment"/></label>
                            <Select
                                options={this.state.listPayment}
                                placeholder={<FormattedMessage id="admin.manage-doctor.payment"/>}
                                name = "selectedPayment"
                            />
                        </div>
                        <div className="col-4 form-group">
                            <label><FormattedMessage id="admin.manage-doctor.province"/></label>
                            <Select
                                options={this.state.listProvince}
                                placeholder={<FormattedMessage id="admin.manage-doctor.province"/>}
                                value = {this.state.selectedProvince}
                                onChange = {this.handleChangeSelectDoctorInfor}
                                name = "selectProvince"
                            />
                        </div>
                        <div className="col-4 form-group">
                            <label><FormattedMessage id="admin.manage-doctor.nameClinic"/></label>
                            <input className="form-control"
                                onChange={(event) => this.handleOnChangeText(event, 'nameClinic')}
                                value={this.state.nameClinic}
                            ></input>
                        </div>
                        <div className="col-4 form-group">
                            <label><FormattedMessage id="admin.manage-doctor.addressClinic"/></label>
                            <input className="form-control"
                                onChange={(event) => this.handleOnChangeText(event, 'addressClinic')}
                                value={this.state.addressClinic}
                            ></input>
                        </div>
                        <div className="col-4 form-group">
                            <label><FormattedMessage id="admin.manage-doctor.note"/></label>
                            <input className="form-control"
                                onChange={(event) => this.handleOnChangeText(event, 'note')}
                                value={this.state.note}
                            ></input>
                        </div>
                    </div>
                    <div className="manage-doctor-editor">
                        <MdEditor
                            style={{height: '500px'}}
                            renderHTML = {text => mdParser.render(text)}
                            onChange = {this.handleEditorChange}
                            value = {this.state.contentMarkdown}
                        />
                    </div>
                    <button
                    onClick = {() => this.handleSaveContentMarkdown()}
                    className= {hasOldData === true ? "save-content-doctor": "create-content-doctor"}
                    >{hasOldData === true ? 
                    <span><FormattedMessage id="admin.manage-doctor.save"/></span> 
                    : 
                    <span><FormattedMessage id="admin.manage-add"/></span>
                    }
                    </button>
                </div>

            
        )
    }
}const mapStateToProps = state => {
    return {
        language: state.app.language,
        allDoctors: state.admin.allDoctors,
        allRequiredDoctorInfor: state.admin.allRequiredDoctorInfor,
    };
};
const mapDispatchToProps = dispatch => {
    return {
        fetchAllDoctors: () => dispatch(actions.fetchAllDoctors()),
        saveDetailDoctors: (data) => dispatch(actions.saveDetailDoctors(data)),
        getAllRequiredDoctorInfor: () => dispatch(actions.getRequiredDoctorInfor()),
    };
};
export default connect(mapStateToProps, mapDispatchToProps)(ManageDoctor);
