        }
    }
    showHideDetalInfor = (status) => {
        this.setState({
            isShowDetailInfor: status
        })
    }
    render(){
        let {isShowDetailInfor} = this.state;
        return(
            <div className="doctor-extra-infor-container">
                <div className="content-up">
                    <div className="text-address">DIA CHI KHAM</div>
                    <div className="name-clinic">Phong kham chuyen khoa da lieu</div>
                    <div className="detail-address">147 Trieu Khuc - Thanh Xuan</div>
                </div>
                <div className = "content-down">
                    {isShowDetailInfor === false &&
                        <div className="short-infor">
                            Gia Kham: 250.000D.
                            <span onClick={() => this.showHideDetalInfor(true)}>
                                Xem chi tiet
                            </span>
                        </div>
                    }
                    {isShowDetailInfor === true &&
                        <>
                            <div className="title-price">Gia Kham:. </div>
                            <div className="detal-infor">
                                <div className="price">
                                    <span className="left">Gia Kham</span> 
                                    <span className="right">250.000d</span> 
                                </div>
                                <div className="note">
                                    Duoc uu tien
                                </div>
                            </div>
                            <div className="payment">
                                Tra phi
                            </div>
                            <div className="hide-price">
                                <span onClick={() => this.showHideDetalInfor(false)}>An bang gia</span>
                            </div>
                        </>
                    }
            </div>
            </div>
        )

    }
}
const mapStateToProps = state => {
    return {
        language: state.app.language
    };
};

const mapDispatchToProps = dispatch => {
