import React, {Component} from "react";
import { connect } from "react-redux";
import { FormattedMessage } from "react-intl";
import './DetailSpecialty.scss';
import HomeHeader from '../../HomePage/HeaderHome';
import DoctorSchedule from '../Doctor/DoctorSchedule';
import DoctorExtraInfor from "../Doctor/DoctorExtraInfor";
import ProfileDoctor from "../Doctor/ProfileDoctor";
import {getAllDetailSpecialty, getAllCodeService} from '../../../services/userService';
import _ from 'lodash';
import {LANGUAGES} from '../../../utils';

class DetailSpecialty extends Component {
    constructor(props){
        super(props);
        this.state ={
            arrDoctorId: [],
            dataDetailSpecialty: {},
            listProvince: []
        }
    }

    async componentDidMount(){
        if (this.props.match && this.props.match.params && this.props.match.params.id) {
            let id = this.props.match.params.id;

            let res = await getAllDetailSpecialtyById({
                id: id,
                location: 'ALL'
            });

            let resProvince = await  getAllCodeService('PROVINCE');
            if (res && res.errCode === 0 && resProvince && resProvince.errCode === 0){
                let data = res.data;
                let arrDoctorId= [];
                if (data && !_.isEmpty(res.data)){
                    let arr = data.doctorSpecialty;
                    if (arr && arr.length > 0){
                        arr.map(item => {
                            arrDoctorId.push(item.doctorId)
                        })
                    }
                }

                this.setState({
                    dataDetailSpecialty: res.data,
                    arrDoctorId : arrDoctorId,
                    listProvince: resProvince.data
                })
            }
        }
    }

    async componentDidUpdate(prevProps, prevState, snapshot){
        if (this.props.language !== prevProps.language){

        }
    }

    handleOnchangeSelect = (event) => {
        console.log('check onchange:', event.target.value)
    }

    render(){
        let {arrDoctorId, dataDetailSpecialty, listProvince} = this.state;
        console.log('check state', this.state)
        let {language} = this.props;
        return(
            <div className="detail-specialty-container">
                <HomeHeader />
                <div className="detail-specialty-body">
                    <div className="description-spcialty">
                        {dataDetailSpecialty && !_isEmply(dataDetailSpecialty)
                        &&
                        <div dangerouslySetInnerHTML={{__html: dataDetailSpecialty.descriptionHTML}}>

                        </div>
                        }
                    </div>
                    <div className="search-sp-doctor">
                        <select onChange = {(event) => this.handleOnchangeSelect(event)}>
                            {listProvince && listProvince.length > 0 &&
                            listProvince.map((item, index) => {
                                return(
                                    <option key={index} value= {item.keyMap}>
                                        {language === LANGUAGES.VI ? item.valueVi : item.valueEn}
                                    </option>
                                )
                            })}
                        </select>
                    </div>
                    {arrDoctorId && arrDoctorId.length > 0 &&
                        arrDoctorId.map((item, index) => {
                            return (
                                <div className="each-doctor" key= {index}>
                                    <div className="dt-content-left">
                                        <div className="profile-doctor">
                                            <ProfileDoctor
                                                doctorId={item}
                                                isShowDescriptionDoctor={true}
                                            />    
                                        </div>
                                    </div>
                                    <div className="dt-content-right">
                                        <div className="doctor-schdule">
                                            <DoctorSchedule
                                                doctorIdFromParent={item}
                                            />

                                        </div>
                                        <div className="doctor-extra-infor">
                                            <DoctorExtraInfor
                                                doctorIdFromParent={item}
                                            />

                                        </div>
                                    </div>
                                </div>
                            )
                        })}
                </div>
            </div>

        );
    }
}

const mapStateToProps = state => {
    return{
        language: state.app.language,

    };
};
const mapDispatchToProps = dispath => {
    return {};
};
export default connect(mapStateToProps, mapDispatchToProps)(DetailSpecialty);
