import React, {Component} from "react";
import {connect} from "react-redux";
import { FormattedMessage } from "react-intl";
import './RemedyModal.scss';
import {Modal, Button, ModalHeader, ModalBody, ModalFooter} from 'reactstrap';
import {toast} from "react-toastify";
import moment from "moment";
import {CommonUtils} from "../../../utils";

class RemedyModal extends Component {
    constructor(props){
        super(props);
        this.state = {
            ElementInternals: '',
            imgBase64: '',

        }
    }
    handleSemdRemedy = ()=> {
        this.props.sendRemedy(this.state)
    }
    async componentDidMount(){
        if (this.props.dataModal){
            this.setState({
                email: this.props.dataModal.email
            })
        }
    }

    async componentDidUpdate(prevProps, prevState, snapshot){
        if (prevProps.dataModal !== prevProps.dataModal){
            this.setState({
                email: this.props.dataModal.email
            })
        }

    }

    handleOnchangeInput = (event, id) =>  {
        let valueInput = event.target.value;
        let stateCopy = {...this.state};
        stateCopy[id] = valueInput;
        this.setState({
            ...stateCopy
        })
    }

    handleOnchangeEmail = (event) => {
        this.setState({
            email: event.target.value
        })
    }

    handleOnchangeImage = async (event) => {
        let data = event.target.files;
        let file = data[0];
        if(file) {
            let base64 = await CommonUtils.getBase64(file);
            this.setState({
                imgBase64: base64
            })
        }
    }
    handleSendRemedy = () => {
        this.props.sendRemedy(this.state)
    }
    render(){
        //toggle={}
        let {isOpenModal, closeBookingClose, dataModal, sendRemedy} = this.props;
        return (
            <Modal
                isOpen={isOpenModal}
                className={'booking-modal-container'}
                size="md"
                centered
                //backdrop={true}
            >
                <div className="modal-header">
                    <h5 className="modal-title">Gui hoa don kham benh thanh cong</h5>
                    <button  type="button" className="close" aria-lable="close" onClick={closeRemedyModal}>
                        <span aria-hidden="true">X</span>
                    </button>
                </div>
                <ModalBody>
                    <div className="row">
                        <div className="col-6 form-group">
                            <label>Email benh nhan</label>
                            <input className="form-control" type ="email" value={this.state.enail}
                                onChange = {(event) => this.handleOnchangeEmail(event)} />
                        </div>
                        <div className="col-6 form-group">
                            <label>Chon file don thuoc</label>
                            <input className="form-control-file" type="file"
                                onChange={(event) => this.handleOnchangeImage(event)}
                                />
                        </div>
                    </div>
                </ModalBody>
                <ModalFooter>
                    <Button color="primary" onClick={sendRemedy}>Send</Button>{''}
                    <Button color="secondary" onClick={closeRemedyModal}>cancel</Button>
                </ModalFooter>
            </Modal>    
        );
    }
}
const mapDispatchToProps = dispatch => {
    return {
        getGenders: () => dispatch(actions.fetchGenderStart()),
    };
};
const mapStateToProps = state => {
    return {
        language: state.app.language,
        genders: state.admin.genders,
    };
};
export default connect(mapStateToProps, mapDispatchToProps)(RemedyModal);
